#[derive(Debug, Clone)]
pub struct UpdateAgentCapabilities {
    /// Agent ID
    pub id: Uuid,
    /// Capabilities to add
    pub add_capabilities: Vec<String>,
    /// Capabilities to remove
    pub remove_capabilities: Vec<String>,
}

impl Command for UpdateAgentCapabilities {
    type Aggregate = crate::Agent;
}

